csFiles.embedded.css:
.embedded-card {
  --card-primary-color: #000;
  --card-secondary-color: var(--boxel-medium-light-purple);

  width: 100%;
  max-width: 450px;
  background-color: #fff;
  border-radius: var(--boxel-border-radius);
  border: var(--boxel-border);
  color: var(--card-primary-color);
  font: var(--boxel-font);
  letter-spacing: var(--boxel-lsp);
  transition: box-shadow var(--boxel-transition);
}

.embedded-card:hover {
  box-shadow: var(--boxel-box-shadow);
}

.embedded-card:hover .embedded-card__header {
  color: var(--boxel-medium-grey);
}

.embedded-card__header {
  height: 30px;
  padding: 7px 15px;
  border-radius: 10px 10px 0 0;
  background-color: var(--boxel-light-grey);
  color: var(--boxel-light-purple);
  font: var(--boxel-font-xs);
  font-weight: 600;
  letter-spacing: var(--boxel-lsp-xl);
  text-transform: uppercase;
  transition: background-image var(--boxel-transition), color var(--boxel-transition);
}

.embedded-card__inner {
  position: relative;
  display: grid;
  align-items: center;
  gap: 3px 15px;
  grid-template:
  "img ."     auto
  "img ."     auto
  "img title" auto
  "img desc"  auto
  "img ."     auto
  "img ."     auto / auto 1fr;
  min-height: 90px;
  padding: 15px;
  font: var(--boxel-font);
  letter-spacing: var(--boxel-lsp);
  overflow: hidden;
}

.embedded-card__title {
  grid-area: title;
  align-self: end;
  margin: 0;
  font: inherit;
  font-weight: 700;
  letter-spacing: inherit;
}

.embedded-card__description {
  grid-area: desc;
  align-self: start;
  margin: 0;
  color: var(--card-secondary-color);
  font: var(--boxel-font-sm);
  letter-spacing: inherit;
  overflow-wrap: break-word;
  word-break: break-word;
}

.embedded-card__bg-img {
  grid-column: 1;
  grid-area: img;
  width: 50px;
  height: 50px;
  background-image: url('https://cardstack.github.io/boxel/media-registry/placeholders/profile-4b36b82cc8d2cee21c28a022c9988a75.svg');
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  border-radius: 100px;
}


/* Fields */
.embedded-card__fields {
  list-style-type: none;
  margin: 0;
  padding-left: 0;
  padding-top: 25px;
  grid-column: 1 / -1;
  display: grid;
  grid-template-columns: repeat(4, auto);
  justify-items: start;
  gap: 30px;
  white-space: nowrap;
  overflow: hidden;
}

.embedded-card__field .field {
  grid-template-columns: auto;
  gap: 3px;
  padding: 0;
  justify-items: start;
}

.embedded-card__field .label {
  color: var(--card-secondary-color);
  font: var(--boxel-font-xs);
  font-weight: 700;
  letter-spacing: var(--boxel-lsp-xl);
  text-transform: uppercase;
  text-align: left;
}

.embedded-card__field .value {
  font: var(--boxel-font-sm);
}

csFiles.isolated.css:
/* Base Styles */
.isolated-card {
  color: #000;
  font: var(--boxel-font);
  letter-spacing: var(--boxel-lsp);
}


/* Header */
.isolated-card__header {
  height: 270px;
  max-width: 845px;
  margin: 0 auto;
  padding: 10px;
  display: grid;
  grid-template-columns: 270px 1fr;
  align-items: center;
  gap: 0 30px;
  border-bottom: var(--boxel-border);
}

.isolated-card__label {
  position: absolute;
  top: 15px;
  left: 20px;
  color: var(--boxel-light-purple);
  font: var(--boxel-font-xs);
  font-weight: 600;
  letter-spacing: var(--boxel-lsp-xl);
  text-transform: uppercase;
}

.isolated-card__img {
  justify-self: center;
  width: 150px;
  height: 150px;
  border-radius: 100px;
  background-image: url('https://cardstack.github.io/boxel/media-registry/placeholders/profile-4b36b82cc8d2cee21c28a022c9988a75.svg');
  background-position: center;
  background-repeat: no-repeat;
  background-size: contain;
}

.isolated-card__title {
  margin: 0;
  font: var(--boxel-font-lg);
  font-weight: 700;
  letter-spacing: 0;
}

.isolated-card__description {
  margin: 0;
  color: var(--boxel-medium-light-purple);
  font: inherit;
  letter-spacing: inherit;
}


/* Section */
.media-detail-section {
  display: grid;
  gap: 10px;
  padding: 25px 0 60px;
}

.media-detail-section__contents {
  padding: 0 50px;
}


/* Fields */
.field {
  display: grid;
  grid-template-columns: 190px 450px;
  gap: 30px;
  padding: 10px;
  max-width: 690px;
  margin: 0 auto;
}

.label {
  font: var(--boxel-font-sm);
  font-weight: bold;
  letter-spacing: var(--boxel-lsp-xl);
  line-height: 22px;
  text-transform: uppercase;
  text-align: right;
}

.value {
  max-width: 100%;
  letter-spacing: var(--boxel-lsp-sm);
}


/* Field name-specific styles */
.verifi-id-value {
  word-wrap: break-word;
}
